// This code reads the simple list of items from the Merge node and finds
// the Facebook and Instagram data within it.

let fbData = null;
let igData = null;

// 1. Loop through the list of items to identify and store the data for each platform.
for (const item of items) {
  const data = item.json;

  // Identify Facebook data by its unique keys: 'is_published' and 'permalink_url'.
  if (data.is_published !== undefined && data.permalink_url) {
    fbData = data;
  }
  // Identify Instagram data by its unique keys: 'permalink' and 'timestamp'.
  else if (data.permalink && data.timestamp) {
    igData = data;
  }
}

// 2. After checking all items, perform the final check to see if both were found and successful.
if (fbData && fbData.is_published === true && igData && igData.permalink) {
  // If yes, return the single success message.
  return [{
    json: {
      status: "All posts succeeded! 🎉",
      facebook_url: fbData.permalink_url,
      instagram_url: igData.permalink
    }
  }];
} else {
  // If not, return a debug message showing exactly what was found.
  return [{
    json: {
      status: "Condition check failed. ❌",
      debug_info: {
        facebook_data_found: fbData || "Facebook data not found in the list.",
        instagram_data_found: igData || "Instagram data not found in the list."
      }
    }
  }];
}

// Fallout-style prompt generator 1
const inputData = $input.all()[0].json;
const agentData = inputData.output || inputData;
const image1Data = agentData.image1;

const technicalDetails = [
  'highly detailed desaturated gritty garage gym photography',
  'photorealistic industrial garage gym with harsh lighting',
  'cinematic raw garage gym aesthetic with film grain'
];

const selectedTechnical = technicalDetails[Math.floor(Math.random() * technicalDetails.length)];
const finalPrompt = `${image1Data.context}, ${selectedTechnical}, cinematic composition`;

return [{
  json: {
    prompt: finalPrompt,
    imageType: 'title',
    imageIndex: 1,
    text_overlay: agentData.originalTheme,
    socialData: agentData // Pass through all social data
  }
}];

// Similar but for image2/factPart1
const finalPrompt = `${agentData.image2.context}, ${selectedTechnical}, cinematic composition`;
return [{
  json: {
    prompt: finalPrompt,
    imageType: 'fact1',
    imageIndex: 2,
    text_overlay: agentData.factPart1,
    socialData: agentData
  }
}];

// Similar but for image3/factPart2
const finalPrompt = `${agentData.image3.context}, ${selectedTechnical}, cinematic composition`;
return [{
  json: {
    prompt: finalPrompt,
    imageType: 'fact2',
    imageIndex: 3,
    text_overlay: agentData.factPart2,
    socialData: agentData
  }
}];

const items = $input.all();

if (items.length !== 3) {
  return [{ json: { error: `Expected 3 images, got ${items.length}` } }];
}

// Sort by imageIndex to ensure correct order
const sortedItems = items.sort((a, b) => a.json.imageIndex - b.json.imageIndex);

const images = [];
const imageUrls = [];

sortedItems.forEach((item, index) => {
  if (item.json.data && item.json.data[0] && item.json.data[0].url) {
    images.push({
      url: item.json.data[0].url,
      type: item.json.imageType,
      textOverlay: item.json.text_overlay
    });
    imageUrls.push(item.json.data[0].url);
  }
});

// Get social data from first item (they should all be the same)
const socialData = sortedItems[0].json.socialData;

// Create combined caption
const combinedCaption = `${socialData.originalTheme}

💪 ${socialData.factPart1}

🔥 ${socialData.factPart2}

Check out the video on #ShockedHistory

#motivation #fitness #mindset #strength #discipline #garageGym #mentalToughness #success #grind #ironmind #fitnessmotivation #strengthtraining #nevergiveup #pushyourlimits #mindovermatter`;

return [{
  json: {
    images: images,
    imageUrls: imageUrls,
    caption: combinedCaption,
    originalTheme: socialData.originalTheme,
    rowNumber: sortedItems[0].json.socialData.rowNumber || $('Parse Video Titles').first().json.rowNumber
  }
}];

const puppeteer = require('puppeteer-core');

// Function to add text overlay
async function addTextOverlay(imageBuffer, text, options = {}) {
  // CORRECT, FINAL URL IS NOW INCLUDED
  const SERVICE_URL = 'https://uke13.github.io/text-overlay-service/?api=true';

  const {
    fontSize = 48,
    position = 'bottom',
    color = 'white'
  } = options;

  if (!imageBuffer) {
    throw new Error('No image buffer provided');
  }

  const base64Image = imageBuffer.toString('base64');
  const imageDataUrl = `data:image/jpeg;base64,${base64Image}`;

  const browser = await puppeteer.launch({
    executablePath: '/usr/bin/chromium',
    headless: true,
    args: [
      '--no-sandbox',
      '--disable-setuid-sandbox',
      '--disable-dev-shm-usage',
      '--disable-gpu'
    ]
  });

  const page = await browser.newPage();
  await page.goto(SERVICE_URL, { waitUntil: 'networkidle0' });
  await page.waitForSelector('canvas', { timeout: 10000 });

  const result = await page.evaluate(async (imageDataUrl, text, fontSize, position, color) => {
    return new Promise((resolve, reject) => {
      const timeout = setTimeout(() => reject(new Error('Timeout waiting for text overlay processing')), 30000);

      const messageHandler = (event) => {
        clearTimeout(timeout);
        window.removeEventListener('message', messageHandler);

        if (event.data.success) {
          resolve(event.data);
        } else {
          reject(new Error(event.data.error || 'Processing failed'));
        }
      };

      window.addEventListener('message', messageHandler);

      window.postMessage({
        imageUrl: imageDataUrl,
        text: text,
        fontSize: parseInt(fontSize),
        position: position,
        color: color
      }, '*');
    });
  }, imageDataUrl, text, fontSize, position, color);

  await browser.close();

  if (!result.success) {
    throw new Error(result.error || 'Processing failed');
  }

  const processedBase64 = result.imageData.split(',')[1];
  return Buffer.from(processedBase64, 'base64');
}

