  const imageBuffer = $binary.data;
  const textOverlay = $json.text_overlay;
  const imageType = $json.imageType || 'unknown';

  // Different font sizes for different image types
  const fontSizes = {
    'title': 64,
    'fact1': 56,
    'fact2': 48
  };

  const fontSize = fontSizes[imageType] || 48;

  console.log(`Processing ${imageType} with text: "${textOverlay}"`);

  const processedBuffer = await addTextOverlay(imageBuffer, textOverlay, {
    fontSize: fontSize,
    position: 'bottom',
    color: 'white'
  });

  return [{
    binary: {
      data: processedBuffer,
      mimeType: 'image/jpeg',
      fileName: `${imageType}-with-text-${Date.now()}.jpg`
    },
    json: {
      success: true,
      originalText: textOverlay,
      imageType: imageType,
      fontSize: fontSize
    }
  }];

} catch (error) {
  console.error('Text overlay failed:', error);

  // Return original image on failure
  return [{
    binary: {
      data: $binary.data,
      mimeType: 'image/jpeg',
      fileName: `${$json.imageType || 'image'}-original-${Date.now()}.jpg`
    },
    json: {
      error: error.message,
      fallback: 'returned_original_image'
    }
  }];
}

