// Get all input items (each item is a row)
const inputItems = $input.all();

if (!inputItems || inputItems.length === 0) {
  return [{ json: { error: 'No rows returned from Google Sheets' } }];
}

const availableTitles = [];

// Each input item is a row object
for (let i = 0; i < inputItems.length; i++) {
  const rowData = inputItems[i].json;

  const title = rowData.TITLE;
  const photoPosted = rowData['PHOTO POSTED'];

  // Skip if no title or if it's a header
  if (!title || title.toLowerCase() === 'title') continue;

  // Check if PHOTO POSTED is empty
  if (title.trim().length > 0 && (!photoPosted || photoPosted.toString().trim() === '')) {
    availableTitles.push({
      title: title.trim(),
      rowNumber: rowData.row_number,
      date: rowData.DATE,
      description: rowData.DESC
    });
  }
}

if (availableTitles.length === 0) {
  return [{ json: { 
    error: 'No available titles found',
    totalRows: inputItems.length,
    sampleRow: inputItems[0].json
  }}];
}

// Random selection
const randomIndex = Math.floor(Math.random() * availableTitles.length);
const randomSelection = availableTitles[randomIndex];

console.log(`Selected "${randomSelection.title}" (${randomIndex + 1} of ${availableTitles.length} available)`);

return [{
  json: {
    selectedTitle: randomSelection.title,
    rowNumber: randomSelection.rowNumber,
    availableCount: availableTitles.length,
    selectedDate: randomSelection.date
  }
}];

// Get the agent's response
const inputData = $input.all()[0].json;
const agentData = inputData.output || inputData;
const originalTheme = agentData.originalTheme;
const context = agentData.context;

// Check for necessary input
if (!originalTheme || !context) {
  return [{
    json: {
      error: 'Missing originalTheme or context from the upstream agent.',
      receivedData: agentData
    }
  }];
}

// --- REVISED & MORE AGGRESSIVE STYLE DESCRIPTORS ---
const styleDescriptors = [
  'ultrawide shot on gritty 35mm film, brutalist concrete gym, dramatic chiaroscuro lighting, heavy atmospheric dust, in the style of a Fallout game screenshot, desaturated, focus on environmental storytelling, no characters too close to the foreground',
  'wide angle hyperrealistic render, vast post-apocalyptic setting, rusted weights scattered in the environment, volumetric god rays, stark and emotionless figure in the mid-distance',
  'cinematic ultrawide photo, industrial grunge aesthetic, expansive weathered metal walls, cracked concrete stretching into the distance, single harsh light source illuminating the scene, figures in the background',
  'dieselpunk art style, panoramic view of utilitarian machinery and desolate landscape, deeply desaturated muted tones, photorealistic texture, rendered in Unreal Engine 5, emphasis on the scale of the environment',
  'raw, ultrawide cinematic photo, lone subject with an unflinching gaze, vast decaying industrial surroundings, high contrast black and white, emphasizing the environment\'s scale, subject not in the immediate foreground'
];

// Pick a random style descriptor
const selectedStyle = styleDescriptors[Math.floor(Math.random() * styleDescriptors.length)];

// Combine the AGENT'S context (the scene) with our technical style (the look)
const finalPrompt = `${context}, ${selectedStyle}, cinematic composition`;

console.log(`Applied style "${selectedStyle}" to context for theme "${originalTheme}"`);

// Return the final structured data
return [{
  json: {
    prompt: finalPrompt,
    motivationalTheme: originalTheme,
    context: context, // The original, unmodified context
    technicalStyle: selectedStyle, // The style we added
    generatedAt: new Date().toISOString()
  }
}];

