// Get the AI's response which contains the final calculated totals
const aiResponse = JSON.parse($input.first().json.choices[0].message.content);
const userMessage = $('Telegram Trigger1').item.json.message;

// Combine the AI response with user and time data
const result = {
  ...aiResponse, // This adds food_name, calories, protein, etc. from the AI
  date: new Date().toISOString().split('T')[0],
  time: new Date().toLocaleTimeString('en-US', {
    hour12: false,
    hour: '2-digit',
    minute: '2-digit',
    timeZone: 'America/Chicago'
  }),
  telegram_user_id: userMessage.from.id,
  telegram_username: userMessage.from.username || userMessage.from.first_name
};

return { json: result };

// 1. Get all of today's meals from the input.
const notionResults = $input.all();

// 2. Find the MOST RECENT meal by sorting the list by creation time.
notionResults.sort((a, b) => new Date(b.json.created_time) - new Date(a.json.created_time));
const mostRecentMeal = notionResults[0];

// 3. Extract the details of that most recent meal for the summary.
const mealProps = mostRecentMeal.json.properties;
const currentMeal = {
  // THIS LINE IS NOW CORRECTED to use 'Food Item'
  food_name: mealProps['Food Item']?.title[0]?.plain_text || 'Unknown Meal',
  calories: mealProps['Calories']?.number || 0,
  protein: mealProps['Protein (g)']?.number || 0,
  fat: mealProps['Fat (g)']?.number || 0,
  carbs: mealProps['Carbs (g)']?.number || 0,
  fiber: mealProps['Fiber (g)']?.number || 0
};

// 4. Calculate daily totals using ALL of today's meals.
let dailyTotals = { calories: 0, protein: 0, fat: 0, carbs: 0, fiber: 0 };

notionResults.forEach(item => {
  const itemProps = item.json.properties;
  dailyTotals.calories += itemProps['Calories']?.number || 0;
  dailyTotals.protein += itemProps['Protein (g)']?.number || 0;
  dailyTotals.fat += itemProps['Fat (g)']?.number || 0;
  dailyTotals.carbs += itemProps['Carbs (g)']?.number || 0;
  dailyTotals.fiber += itemProps['Fiber (g)']?.number || 0;
});

// 5. Round the totals.
Object.keys(dailyTotals).forEach(key => {
  dailyTotals[key] = Math.round(dailyTotals[key]);
});

// 6. Create the final response message.
const mealSummary = `🍽️ **Meal Logged: ${currentMeal.food_name}**\n` +
  `📊 **This Meal:**\n` +
  `• Calories: ${currentMeal.calories}\n` +
  `• Protein: ${currentMeal.protein}g\n` +
  `• Fat: ${currentMeal.fat}g\n` +
  `• Carbs: ${currentMeal.carbs}g\n` +
  `• Fiber: ${currentMeal.fiber}g\n\n` +
  `📈 **Today's Totals:**\n` +
  `• Calories: ${dailyTotals.calories}\n` +
  `• Protein: ${dailyTotals.protein}g\n` +
  `• Fat: ${dailyTotals.fat}g\n` +
  `• Carbs: ${dailyTotals.carbs}g\n` +
  `• Fiber: ${dailyTotals.fiber}g`;

// 7. Return the final object for the Telegram node.
return [{
  json: {
    message: mealSummary,
    chat_id: $('Telegram Trigger1').item.json.message.chat.id,
    dailyTotals: dailyTotals,
  },
}];

