const originalData = $('Calculate Nutrition1').item.json;

// Get the verified data from the verifier AI
// Note: The AI Agent node might put the response in a different place than the HTTP node.
// We'll check for a 'content' or direct property.
const aiResponse = $input.first().json;
const verifiedDataString = aiResponse.content || JSON.stringify(aiResponse);
const verifiedData = JSON.parse(verifiedDataString);


// Combine them into the final object for Notion
const result = {
  ...verifiedData, // Use the verified food_name, calories, protein, etc.
  date: originalData.date,
  time: originalData.time,
  telegram_user_id: originalData.telegram_user_id,
  telegram_username: originalData.telegram_username,
};

return { json: result };

// Get the first item from the input array.
// In "Run Once for All Items" mode, 'items' is an array of all input items.
console.log('--- Extract Search Term: Starting execution ---');
console.log('Input items received:', items.length, 'items');

if (!items || items.length === 0) {
    console.error('Error: No input items. Exiting gracefully.');
    return []; // Return an empty array if no input, to prevent crashing on items[0]
}

const currentItem = items[0];
console.log('First item structure (partial):', JSON.stringify(currentItem.json.message));

// Access the message from the currentItem's JSON data
// Add a check to ensure 'text' property exists and is a string
if (!currentItem.json || !currentItem.json.message || typeof currentItem.json.message.text !== 'string') {
    console.error('Error: Message text is missing or not a string. Exiting.');
    return [];
}
const message = currentItem.json.message.text.toLowerCase();
console.log('Original message (lowercase):', message);

// List of keywords to remove from the message to find the search term
const keywords = [
  'my', 'the', 'a', 'an', 'meal',
  'normal', 'usual', 'typical', 'daily',
  'favorite', 'standard', 'regular',
  'go-to', 'again', 'repeat', 'same as'
];

let searchTerm = message;
console.log('Initial searchTerm:', searchTerm);

for (const keyword of keywords) {
  // This removes the keyword as a whole word
  const regex = new RegExp(`\\b${keyword}\\b`, 'gi');
  console.log(`Applying regex /\\b${keyword}\\b/gi to: "${searchTerm}"`);
  searchTerm = searchTerm.replace(regex, '');
  console.log('searchTerm after keyword removal:', searchTerm);
}

// Clean up any extra spaces left behind and add it to the current item's JSON
// ****** THIS IS THE CRITICAL CHANGE ******
currentItem.json.searchWords = searchTerm.split(' ').filter(word => word.length > 0);
console.log('Final searchTerm added to item.json:', currentItem.json.searchTerm);

// Return the modified array of items
console.log('--- Extract Search Term: Returning items ---');
return items;

// Get the search words from the 'Extract Search Term' node
const searchWords = $('Extract Search Term').item.json.searchWords;

// Rank each item from Notion based on how many search words its title contains
const rankedItems = items.map(item => {
  // Safety check to skip empty items
  if (!item.json || typeof item.json.property_food_item !== 'string' || item.json.property_food_item === '') {
    return { item: item, matchCount: 0 };
  }

  const title = item.json.property_food_item.toLowerCase();

  // Count how many search words are found in the title
  let count = 0;
  for (const word of searchWords) {
    if (title.includes(word)) {
      count++;
    }
  }

  return { item: item, matchCount: count };
});

// Filter out any items that had zero matches
const matchedItems = rankedItems.filter(rankedItem => rankedItem.matchCount > 0);

// Sort the results so the meal with the highest score is first
matchedItems.sort((a, b) => b.matchCount - a.matchCount);

// Return the sorted list of meals
return matchedItems.map(rankedItem => rankedItem.item);

// The input 'items' is an array of all matching meals.
// We only want to log the first one.
const firstMatch = items[0];

// Return just the single item for the next node.
return firstMatch;
