const originalData = $('Calculate Nutrition1').item.json;

// Get the verified data from the verifier AI
// Note: The AI Agent node might put the response in a different place than the HTTP node.
// We'll check for a 'content' or direct property.
const aiResponse = $input.first().json;
const verifiedDataString = aiResponse.content || JSON.stringify(aiResponse);
const verifiedData = JSON.parse(verifiedDataString);


// Combine them into the final object for Notion
const result = {
  ...verifiedData, // Use the verified food_name, calories, protein, etc.
  date: originalData.date,
  time: originalData.time,
  telegram_user_id: originalData.telegram_user_id,
  telegram_username: originalData.telegram_username,
};

return { json: result };
