// Test if Sharp is available
try {
  const sharp = require('sharp');
  console.log('Sharp version:', sharp.versions);

  return [{
    json: {
      sharp_available: true,
      sharp_version: sharp.versions.sharp,
      vips_version: sharp.versions.vips,
      message: "Sharp is available! ✅"
    }
  }];

} catch (error) {
  console.log('Sharp error:', error.message);

  return [{
    json: {
      sharp_available: false,
      error: error.message,
      message: "Sharp is NOT available ❌",
      suggestion: "Try Canvas or external API instead"
    }
  }];
}
