/**
 * Build HTML optimized for DOCX conversion.
 * Expects:
 * $json.coverLink       (string)  – URL to the cover image
 * $json.ebookMarkdown   (string)  – Markdown content for the book
 *
 * Returns:
 * json.html             (string)  – Complete HTML string
 */

/* -------------------------------------------------------------------------- */
/* PULL INPUT VARIABLES                                                       */
/* -------------------------------------------------------------------------- */
const coverUrl     = $json.coverLink        || '';
const bodyMarkdown = $json.ebookMarkdown    || '# Content Missing\n\nNo content was provided for the ebook.';

/* -------------------------------------------------------------------------- */
/* MARKDOWN TO HTML CONVERTER                                                 */
/* -------------------------------------------------------------------------- */
function mdToHtml(md) {
  if (typeof md !== 'string') {
    console.log("N8N_LOG_DEBUG --- mdToHtml received non-string input:", md);
    return '<p>Invalid content provided.</p>';
  }

  let html = md
    .replace(/^### (.*)$/gm, '<h3>$1</h3>')
    .replace(/^## (.*)$/gm,  '<h2>$1</h2>')
    .replace(/^# (.*)$/gm,   '<h1>$1</h1>')
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.*?)\\*/g,     '<em>$1</em>');

  const lines = html.split('\n');
  let inList = false;
  let listType = ''; // 'ul' or 'ol'
  let processedHtmlLines = [];

  for (let i = 0; i < lines.length; i++) {
    let line = lines[i];
    const trimmedLine = line.trim();
    const isUlItem = trimmedLine.startsWith('* ');
    const isOlItem = /^\d+\.\s/.test(trimmedLine);

    if (isUlItem || isOlItem) {
      const currentListType = isUlItem ? 'ul' : 'ol';
      if (!inList || listType !== currentListType) {
        if (inList) {
          processedHtmlLines.push(`</${listType}>`);
        }
        processedHtmlLines.push(`<${currentListType}>`);
        inList = true;
        listType = currentListType;
      }
      const itemContent = isUlItem ? trimmedLine.substring(2) : trimmedLine.replace(/^\d+\.\s/, '');
      processedHtmlLines.push(`  <li>${itemContent.trim()}</li>`);
    } else {
      if (inList) {
        processedHtmlLines.push(`</${listType}>`);
        inList = false;
      }
      if (trimmedLine === '') {
        // Skip
      } else if (!(/^<(h[1-6]|p|ul|ol|li|blockquote|hr)/i.test(trimmedLine))) {
        processedHtmlLines.push(`<p>${trimmedLine}</p>`);
      } else {
        processedHtmlLines.push(line);
      }
    }
  }

  if (inList) {
    processedHtmlLines.push(`</${listType}>`);
  }

  return processedHtmlLines.filter(line => line.trim() !== '<p></p>' && line.trim() !== '').join('\n');
}

/* -------------------------------------------------------------------------- */
/* ASSEMBLE FINAL HTML FOR DOCX                                               */
/* -------------------------------------------------------------------------- */
const bodyHtmlContent = mdToHtml(bodyMarkdown);

const fullHtml = `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="utf-8">
  <title>Ebook</title>
  <style>
    body {
      font-family: Arial, Helvetica, sans-serif;
      font-size: 16pt;
      line-height: 1.4;
      color: #000000;
      margin: 2cm 1.5cm 2cm 1.5cm;
    }
    p { margin-top: 0; margin-bottom: 0.6em; }
    h1, h2, h3 {
      font-size: 20pt;
      font-weight: bold;
      color: #000000;
      margin-top: 1.2em;
      margin-bottom: 0.4em;
      line-height: 1.2;
      page-break-after: avoid;
    }
    .page-break-docx {
      page-break-after: always;
      height: 0pt;
      line-height: 0pt;
      font-size: 0pt;
    }
    strong, b { font-weight: bold; }
    em, i { font-style: italic; }
    ul, ol { margin-top: 0.3em; margin-bottom: 0.7em; padding-left: 1.8em; }
    li { margin-bottom: 0.2em; }
    .cover-image-container {
      text-align: center;
      margin: 0 auto 2cm auto;
      page-break-after: always;
    }
  </style>
</head>
<body>
    <!-- Simplified image approach for better DOCX compatibility -->
    <div class="cover-image-container">
      <img src="${coverUrl}" alt="Ebook Cover" style="width:15cm; height:auto; max-width:100%;" />
    </div>

  ${bodyHtmlContent}

</body>
</html>`;  // <-- THIS CLOSING BACKTICK WAS MISSING!

/* -------------------------------------------------------------------------- */
/* RETURN RESULT                                                              */
/* -------------------------------------------------------------------------- */
return [
  {
    json: { html: fullHtml }
  }
];

