/**
 * Build HTML (Value Enhancer) for DOCX conversion.
 * Returns binary HTML for the next "Binary Data1" node.
 */

// 1) Grab and parse VE JSON from the correct path
const rawOpenAiOutput = $node["Value Enhancer"].json;
let veData = {}; // This will hold the object {bonuses, workbookOutline, oto}

if (rawOpenAiOutput && rawOpenAiOutput.message && rawOpenAiOutput.message.content) {
    const contentField = rawOpenAiOutput.message.content;
    if (typeof contentField === 'string' && contentField.trim().startsWith('{')) {
        try {
            veData = JSON.parse(contentField);
        } catch (e) {
            console.log("N8N_LOG_ERROR (Build HTML Value Enhancer): Failed to parse AI content as JSON. Content:", contentField, "Error:", e);
            // veData remains {}
        }
    } else if (typeof contentField === 'object' && contentField !== null) {
        veData = contentField; // It's already an object
    } else {
        console.log("N8N_LOG_ERROR (Build HTML Value Enhancer): AI content is not a string or object. Content:", contentField);
        // veData remains {}
    }
} else {
    console.log("N8N_LOG_ERROR (Build HTML Value Enhancer): Expected path .message.content not found in Value Enhancer output. Raw Output:", JSON.stringify(rawOpenAiOutput));
    // veData remains {}
}

// 2) Safe-default arrays/objects using the extracted veData
const bonuses  = Array.isArray(veData.bonuses) ? veData.bonuses : [];
const workbook = Array.isArray(veData.workbookOutline) ? veData.workbookOutline : []; // Check AI prompt for exact key name
const oto      = (veData.oto && typeof veData.oto === 'object') ? veData.oto : { name:"", price:"", description:"", deliverables:[] };

// 3) Helpers → build inner HTML lists
const listItems = (arr, templateFn) => {
  if (!Array.isArray(arr)) return '';
  return arr.map(item => item ? templateFn(item) : '').join(""); // Added check for item
};

const bonusHtml = `<ul>${listItems(bonuses, b =>
  `<li><strong>${b.name || "N/A"}</strong> (${b.format || "N/A"}) – ${b.benefit || "N/A"}</li>`
)}</ul>`;

const workbookHtml = `<ol>${listItems(workbook, w =>
  `<li><strong>${w.section || "N/A"}</strong>: ${w.description || "N/A"}</li>`
)}</ol>`;

// Ensure oto.deliverables is an array before mapping
const otoDeliverables = Array.isArray(oto.deliverables) ? oto.deliverables : [];
const otoHtml = `
  <h2>One-Time Offer — ${oto.name || "N/A"} ($${oto.price || "N/A"})</h2>
  <p>${oto.description || "N/A"}</p>
  <ul>${listItems(otoDeliverables, d => `<li>${d || "N/A"}</li>`)}</ul>
`;

// 4) Assemble full HTML document with DOCX-friendly styles
const fullHtml = `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="utf-8"/>
  <title>Value Enhancer</title>
  <style>
    /* Styles optimized for HTML to DOCX conversion */
    body {
      font-family: Arial, Helvetica, sans-serif;
      font-size: 12pt; /* Approx 16px - standard for documents */
      line-height: 1.4;
      color: #000000;
      margin: 2cm 1.5cm; /* Page Margins: Top/Bottom Left/Right. Adjust as needed. */
    }
    h1 {
      font-size: 18pt; /* Larger for main title */
      font-weight: bold;
      color: #000000;
      margin-top: 0; /* Main title often at the very top */
      margin-bottom: 0.8em;
      text-align: center; /* Optional: Center main title */
    }
    h2 {
      font-size: 14pt; /* Sub-headings slightly smaller */
      font-weight: bold;
      color: #000000;
      margin-top: 1.2em;
      margin-bottom: 0.4em;
      page-break-after: avoid;
    }
    p {
      margin-top: 0;
      margin-bottom: 0.6em;
    }
    ul, ol {
      margin-top: 0.2em;
      margin-bottom: 0.8em;
      padding-left: 1.5em; /* Standard list indentation */
    }
    li {
      margin-bottom: 0.2em;
    }
    strong {
      font-weight: bold;
    }
  </style>
</head>
<body>
  <h1>Bonus Vault &amp; Value Enhancer</h1>

  <h2>Bonus Bundle</h2>
  ${bonusHtml}

  <h2>Workbook Outline</h2>
  ${workbookHtml}

  ${otoHtml}
</body>
</html>`;

// 5) Return as an object with a binary property 'html',
//    which itself is an object containing the data Buffer.
//    This is what "Binary Data1" node expects as input.
return [{
  json: {}, // Keep json part minimal or add useful metadata if needed
  binary: {
    html: { // This is the binary property name "Binary Data1" will look for
      data: Buffer.from(fullHtml, "utf8"), // The HTML content as a Buffer
      mimeType: "text/html",
      fileName: "value-enhancer.html"
    }
  }
}];

// Revised Code for "Binary Data1" Node

// Check if the basic path to the data exists
if (!items[0].binary || !items[0].binary.html || !items[0].binary.html.data) {
  console.log("N8N_LOG_DEBUG: Unexpected input structure in Binary Data1. Item:", JSON.stringify(items[0]));
  throw new Error("Input from 'Build HTML (Value Enhancer)' did not provide data at items[0].binary.html.data as expected.");
}

let inputHtmlContent = items[0].binary.html.data;
let inputHtmlBuffer;

// Try to create a buffer intelligently
if (inputHtmlContent instanceof Buffer) {
  inputHtmlBuffer = inputHtmlContent;
} else if (typeof inputHtmlContent === 'string') {
  inputHtmlBuffer = Buffer.from(inputHtmlContent, 'utf8');
} else if (typeof inputHtmlContent === 'object' && inputHtmlContent.type === 'Buffer' && Array.isArray(inputHtmlContent.data)) {
  // Sometimes Buffers get serialized to objects like { type: "Buffer", data: [...] }
  inputHtmlBuffer = Buffer.from(inputHtmlContent.data);
} else {
  console.log("N8N_LOG_DEBUG: Data at items[0].binary.html.data is not a Buffer, string, or recognized Buffer object. Actual type:", typeof inputHtmlContent, "Value:", JSON.stringify(inputHtmlContent));
  throw new Error("Data at items[0].binary.html.data from 'Build HTML (Value Enhancer)' is not in a recognized format (Buffer, String, or serialized Buffer object).");
}

const inputMimeType = items[0].binary.html.mimeType || 'text/html';
const inputFileName = items[0].binary.html.fileName || 'value-enhancer.html';

items[0].binary.data = {
  data: inputHtmlBuffer.toString('base64'),
  mimeType: inputMimeType,
  fileName: inputFileName
};

delete items[0].binary.html;

return items;

/**
 * Turn the HTML string coming from Build HTML into a binary
 * property called  data  (base-64) so the Drive Upload node
 * can accept it.
 */
const html = $node["Build HTML (ebook)"].json.html;   // or Value Enhancer
const buffer = Buffer.from(html, "utf8");

items[0].binary = {
  data: {
    data: buffer.toString("base64"),
    mimeType: "text/html",
    fileName: "ebook-asset.html"          // or "value-enhancer.html"
  }
};

return items;
